 	  

(function($){
    'use script';
            $('.menu_item_link').on("click", function () {
                $('.overlay-menu__inner').addClass("overlay-menu--expanded");
                $('.sidebar-opened').addClass("sidebar-opened_full");
                $('.overlay-submenu__item').addClass("overlay-submenu__item--show");
                $('.contact-info').addClass("contact_info_hidden");
                $('.overlay-submenu').addClass("overlay-submenu--expanded");
                $('.social-icon').addClass("social-icon_sub");
            });
             $('.overlay-submenu__back').on("click", function () {
                $('.overlay-menu__inner').removeClass("overlay-menu--expanded");
                $('.sidebar-opened').removeClass("sidebar-opened_full");
                $('.overlay-submenu__item').removeClass("overlay-submenu__item--show");
                $('.contact-info').removeClass("contact_info_hidden");
                $('.overlay-submenu').removeClass("overlay-submenu--expanded");
                $('.social-icon').removeClass("social-icon_sub");
            });

}(jQuery));